CustomField.formula:
if(ISPICKVAL(SBQQ__Type__c, 'Amendment'), 
    if( RecordType.DeveloperName = 'Reservataire', 
        if( ISPICKVAL(SBQQ__BillingFrequency__c, 'Monthly'), 
            if( AND(SBQQ__StartDate__c >= TODAY(), DAY(SBQQ__StartDate__c)= 1) , 'OK', 'KO'),
         if(ISPICKVAL(SBQQ__BillingFrequency__c, 'Quarterly'), 
            if( AND(SBQQ__StartDate__c >= TODAY(), DAY(SBQQ__StartDate__c)= 1, OR(MONTH(SBQQ__StartDate__c)= 1, MONTH(SBQQ__StartDate__c)= 4, MONTH(SBQQ__StartDate__c)= 7, MONTH(SBQQ__StartDate__c)= 12)) , 'OK', 'KO'), 
        'KO' )),
    'OK'),
'OK')
